public static function FormsHtml($form, $inputs = array(), $admin = FALSE){  
  if(is_string($form) && in_array($form, self::$allowedWidgets) && method_exists("App\includes\Forms", $form)){
    return self::{$form}($inputs, $admin);
  }
  return false;
}

 
  public static function generatecsrf($tk = FALSE){  
    $sessionProvider = new \EasyCSRF\NativeSessionProvider();
    $easyCSRF = new \EasyCSRF\EasyCSRF($sessionProvider);
   
    if($token = $easyCSRF->generate('csrf')){
      return '<input type="text" name="token" value="'.$token.'"/>';
    } 
  }




//'für Unternehmen'=>'für Unternehmen',  'für Kandidaten'=>'für Kandidaten',
protected static function mainLinksTop($leveltype, $postCounts = ''){
  $nachrichten = '';
  $furkandidaten = '<li><a href="/fuer-kandidaten" style="color: #00527f;">'.e('für Kandidaten').'</a></li>';
  $stellen = '<li><a href="/stellenangebote" style="color: #00527f;">'.e('Stellenangebote').'</a></li>';
 // $stellen = '';
  $kandidaten = '';
  $unternehmen = '<li><a href="/unternehmen" style="color: #00527f;">'.e('für Unternehmen').'</a></li>';
  if($leveltype && $leveltype == 2){
    $stellen = '';
    $furkandidaten = '';
    $kandidaten .= '<li><a href="/kandidaten" style="color: #00527f;">'.e('Kanditaten').'</a></li>';
  }

  if($leveltype && $leveltype == 2){
    $unternehmen = '';
    $furkandidaten = '';
   // $nachrichten .= '<li><a href="/nachrichten" style="color: #00527f;">'.e('Nachrichten').' <span class="nav-unread badge badge-danger  badge-pill">1</span></a>';
    $nachrichten .= self::mailsTop(["leveltype"=>$leveltype, "postCounts"=>$postCounts]);
    $nachrichten .= self::chatsTop(["leveltype"=>$leveltype, "chatCounts"=>$postCounts]);
  }elseif($leveltype && $leveltype == 3){
    $stellen = '<li><a href="/stellenangebote" style="color: #00527f;">'.e('Stellenangebote').'</a></li>';
    $unternehmen = '';
    $furkandidaten = '';
  //  $nachrichten .= '<li><a href="/inbox" style="color: #00527f;">'.e('Nachrichten').' <span class="nav-unread badge badge-danger  badge-pill">2</span></a>';
    $nachrichten .= self::mailsTop(["leveltype"=>$leveltype, "postCounts"=>$postCounts]);
    $nachrichten .= self::chatsTop(["leveltype"=>$leveltype, "chatCounts"=>$postCounts]);
  }

  return $stellen.'
  '.$unternehmen.'
  '.$kandidaten.'
  '.$furkandidaten.'
  <li><a href="/kontakt" style="color: #00527f;">'.e('Kontakt').'</a>
  '.$nachrichten;

} 

protected static function chatsTop($inputs){ 
  $hrfChat = '';
  if($inputs['leveltype'] && $inputs['leveltype'] == 1){
    $hrfChat = '';
  }elseif($inputs['leveltype'] && $inputs['leveltype'] == 2){
    $hrfChat = 'chatnachrichten';
  }elseif($inputs['leveltype'] && $inputs['leveltype'] == 3){
    $hrfChat = 'chat';
  } 

  $content = '<li><div class="d-none d-md-flex">
                <a class="nav-link icon" onclick="location.href = \''.$hrfChat.'\';return;">
                  <i class="fa fa-2x fa-comments-o text-muted"></i>
                  <span class=" nav-unread badge badge-danger badge-pill">'.$inputs['chatCounts']['chats'].'</span>
                </a>
              </div></li>';
      return $content;
}


