protected static function socialsgenerate($inputs){  
  if($inputs['socials']){
    $countAllowed=0;
    $buttons = '';
    foreach($inputs['socialsloginAllowed'] as $val){
      if($val != 2){
        $countAllowed += 1;
      }
    }

    if($countAllowed >= 3){
      $btnStyleValues = array("5"=>"min-width: 3.65rem;", "4"=>"min-width: 4.5rem;", "3"=>"");
      if($countAllowed >= 4){ $btnStyle = $btnStyleValues[$countAllowed]; }else{ $btnStyle = '';}
      foreach($inputs['socials'] as $name => $link){
        $linkName = explode('=', $link);
        if( in_array( strtolower($linkName[1]) , $inputs['socialsloginAllowed'] ) ){
          if(strtolower($linkName[1]) == "linkedin"){ $btnBacground = 'background-color: #027aaf!important;color: white;';}
          elseif(strtolower($linkName[1]) == "xing"){ $btnBacground = 'background-color: #0FAF88!important;color: white;';}else{$btnBacground = '';}

          if($countAllowed == 3){
              $buttons .= '<a href="'.$link.'" style="'.$btnStyle.$btnBacground.'" class="btn btn-sm btn-pill btn-'.strtolower($linkName[1]).'"><i class="fa fa-'.strtolower($linkName[1]).'"></i> '.$linkName[1].' </a>'; 
          }else if($countAllowed >= 4){
              $buttons .= '<a data-id="'.$countAllowed.'" href="'.$link.'" style="'.$btnStyle.$btnBacground.'" class="btn btn-sm btn-pill  btn-'.strtolower($linkName[1]).'"><i class="fa fa-'.strtolower($linkName[1]).'"></i> '.$linkName[1].'</a>'; 
          }
        }
      }
      return $buttons;
    }else{
      return false;
    }
  }
return false;
}


